import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import axios from 'axios';

// Registering the required components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const Profile = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [totalSpent, setTotalSpent] = useState(0);

  useEffect(() => {
    // Fetch subscriptions (You can replace with actual API call)
    const fetchData = async () => {
      try {
        // Sample data to simulate API response
        const sampleSubscriptions = [
          { name: 'Netflix', expiryDate: '2024-10-01', plan: 'Standard', price: 15, image: 'data:image/png;base64,' },
          { name: 'Hulu', expiryDate: '2024-10-15', plan: 'Basic', price: 7, image: 'data:image/png;base64,' },
          { name: 'Disney+', expiryDate: '2024-11-01', plan: 'Premium', price: 10, image: 'data:image/jpeg;base64,' },
          { name: 'Amazon Prime', expiryDate: '2024-12-01', plan: 'Annual', price: 120, image: 'data:image/jpeg;base64,' },
          { name: 'HBO Max', expiryDate: '2024-10-30', plan: 'Standard', price: 14, image: 'data:image/jpeg;base64,' },
          { name: 'Apple TV+', expiryDate: '2024-11-20', plan: 'Monthly', price: 10, image: 'data:image/png;base64,' },
          { name: 'Paramount+', expiryDate: '2024-10-25', plan: 'Basic', price: 6, image: 'data:image/jpeg;base64,' },
          { name: 'Discovery+', expiryDate: '2024-11-10', plan: 'Standard', price: 5, image: 'data:image/jpeg;base64,' },
          { name: 'Peacock', expiryDate: '2024-12-15', plan: 'Premium', price: 10, image: 'data:image/png;base64,' },
        ];

        setSubscriptions(sampleSubscriptions);

        // Calculate total money spent
        const total = sampleSubscriptions.reduce((sum, sub) => sum + sub.price, 0);
        setTotalSpent(total);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  const spending = months.map(() => Math.floor(Math.random() * 50) + 5); // Random spending data

  const data = {
    labels: months,
    datasets: [
      {
        label: 'Monthly Spending on OTT Platforms',
        data: spending,
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        fill: true,
      },
    ],
  };


  return (
    <div className="dashboard bg-gray-100 min-h-screen py-10 px-4">
      <div className="w-full max-w-7xl mx-auto py-10">
        <h1 className="text-3xl font-bold mb-8">Welcome back, Vashu!</h1>

        {/* Overview section */}
        <div className="bg-white p-6 shadow-lg rounded-lg mb-12">
          <h2 className="text-xl font-semibold mb-4">Total Spent This Month</h2>
          <p className="text-2xl font-bold">${totalSpent.toFixed(2)}</p>

          {/* Chart */}
          <div className="mt-6">
            <h2 className="text-xl font-semibold mb-4">Monthly Spending Overview</h2>
            <Line data={data} />
          </div>
        </div>


        {/* Subscription list */}
        <h2 className="text-2xl font-bold mb-6">Your Subscriptions</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {subscriptions.map((sub, index) => (
            <div key={index} className="bg-white p-6 shadow-lg rounded-lg flex flex-col items-center">
              <img src={sub.image} alt={sub.name} className="w-32 h-32 object-cover mb-4 rounded-lg" />
              <h3 className="text-xl font-semibold mb-2">{sub.name}</h3>
              <p className="text-gray-600">Expiry Date: {new Date(sub.expiryDate).toLocaleDateString()}</p>
              <p className="text-gray-600">Plan: {sub.plan}</p>
              <p className="text-gray-600">Cost: ${sub.price.toFixed(2)}/month</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Profile;